import React from 'react';
import { Card, CardContent, CardMedia, Typography, Button, Chip, CardActions } from '@mui/material';
import { makeStyles } from '@mui/material/styles';

const useStyles = makeStyles((theme) => ({
  media: {
    height: 140,
  },
  chip: {
    margin: theme.spacing(0.5),
  },
}));

function ItemCard({ item, onStatusChange }) {
  const classes = useStyles();
  
  return (
    <Card>
      {item.imagem && (
        <CardMedia
          className={classes.media}
          image={item.imagem}
          title={item.titulo}
        />
      )}
      
      <CardContent>
        <Typography gutterBottom variant="h5" component="h2">
          {item.titulo}
        </Typography>
        
        <Chip 
          label={item.tipo === 'perdido' ? 'Perdido' : 'Achado'} 
          color={item.tipo === 'perdido' ? 'secondary' : 'primary'}
          className={classes.chip}
        />
        
        <Chip 
          label={item.status === 'pendente' ? 'Pendente' : 'Resolvido'} 
          color={item.status === 'pendente' ? 'default' : 'primary'}
          className={classes.chip}
        />
        
        <Typography variant="body2" color="textSecondary" component="p">
          <strong>Local:</strong> {item.local}
        </Typography>
        
        <Typography variant="body2" color="textSecondary" component="p">
          <strong>Data:</strong> {new Date(item.data).toLocaleDateString()}
        </Typography>
        
        <Typography variant="body2" color="textSecondary" component="p">
          <strong>Descrição:</strong> {item.descricao}
        </Typography>
        
        <Typography variant="body2" color="textSecondary" component="p">
          <strong>Contato:</strong> {item.contato}
        </Typography>
      </CardContent>
      
      <CardActions>
        {item.status === 'pendente' && (
          <Button 
            size="small" 
            color="primary"
            onClick={() => onStatusChange(item._id, 'resolvido')}
          >
            Marcar como Resolvido
          </Button>
        )}
      </CardActions>
    </Card>
  );
}

export default ItemCard;
